    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
if(jComboBox1.getSelectedIndex()==0)
{jRadioButton1.setText("Butonex");
 jRadioButton2.setText("Cetcip");
 jRadioButton3.setText("Cipladom");
 jRadioButton4.setText("Tumorex");
 }
else if(jComboBox1.getSelectedIndex()==1)
{jRadioButton1.setText("Masutonopa");
 jRadioButton2.setText("Agartunam");
 jRadioButton3.setText("Podaximoos");
 jRadioButton4.setText("Yendulat");
 }
else if(jComboBox1.getSelectedIndex()==2)
{jRadioButton1.setText("Laotomos");
 jRadioButton2.setText("Gatinotp");
 jRadioButton3.setText("Selopitar");
 jRadioButton4.setText("Dumonotex");
 }
else if(jComboBox1.getSelectedIndex()==3)
{jRadioButton1.setText("Butonex");
 jRadioButton2.setText("Cetcip");
 jRadioButton3.setText("Cipladom");
 jRadioButton4.setText("Tumorex");
 JOptionPane.showMessageDialog(this,"Sorry We don't have any medicine for you.\n"
         + "Consult a Doctor For it."); }
    }//GEN-LAST:event_jComboBox1ActionPerformed

    private void jRadioButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButton1ActionPerformed
switch(jComboBox1.getSelectedIndex())
{case 0:jTextField3.setText("12");
        break;
 case 1:jTextField3.setText("11");
        break;
 case 2:jTextField3.setText("8.75");
        break;}
    }//GEN-LAST:event_jRadioButton1ActionPerformed

