    private void CalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalculateActionPerformed
String n = jTextField1.getText();
String no = jTextField2.getText();
double t = Double.parseDouble(jTextField3.getText());
int d = (int)jSpinner1.getValue();
if(d<=0)
{JOptionPane.showMessageDialog(this,"Select Valid No. Of Days.");}
double amt = t*d;
jTextField5.setText(""+amt);
double gst= Math.round((amt*12)/100);
jTextField6.setText(""+gst);
double total=amt+gst;
jTextField7.setText(""+total);
JOptionPane.showMessageDialog(this,n+",you need to pay a sum of Rs."+total);
    }//GEN-LAST:event_CalculateActionPerformed

